from .location import LocationSharing as location
from .cookie import CookieMonster as cookie

class GoogleMaps:

    def __init__(self, username, password, config_path, cookie_filename, debug=False):

        self.cookie = cookie(username, password, config_path, cookie_filename, debug=debug)
        self.location = location(self.cookie.r, debug=debug)

    def run(self):

        # TODO: best practices in this scenario? try? if?
        self.cookie.run()
        self.location.update()
