from .location import LocationSharing as location
from .cookie import CookieMonster as cookie

class GoogleMaps:

    def __init__(self, username, password, cookie_output, debug=False):

        self.cookie = cookie(username, password, cookie_output, debug=debug)
        self.location = location(self.cookie.r, debug=debug)

    def run(self):

        self.cookie.run()
        self.location.update()
