class Localize:

    def __init__(self, location):

        self.cookie_header_referer = self.db(location, 'cookie_header_referer')
        self.cookie_domain = self.db(location, 'cookie_domain')
        self.cookie_login_start = self.db(location, 'cookie_login_start')
        self.cookie_login_success = self.db(location, 'cookie_login_success')
        self.location_header_authority = self.db(location, 'location_header_authority')
        self.location_header_referer = self.db(location, 'location_header_referer')
        self.location_sharing_url = self.db(location, 'location_sharing_url')

    def db(self, location, url):

        locations = {   'US': { 'cookie_header_referer':'https://accounts.google.com',
                                'cookie_domain':'.goole.com',
                                'cookie_login_start':'https://accounts.google.com',
                                'cookie_login_success':'https://myaccount.google.com/?pli=1',
                                'location_header_authority': 'www.google.com',
                                'location_header_referer': 'https://www.google.com',
                                'location_sharing_url':'https://www.google.com/maps/preview/locationsharing/read'}
                    }
        output = locations[location][url]
        return output
